import React, { useState, ChangeEvent } from "react";
import "./App.css";

function App() {
  // Keep Track of Ticker Input
  const [ticker, setTicker] = useState("");

  // Event handler to update the state when the input changes
  const handleInputChange = (event: ChangeEvent<HTMLInputElement>) => {
    setTicker(event.target.value);
    console.log(event.target.value);
  };

  // Event handler to submit the ticker
  const SubmitNewTicker = () => {
    const RequestOptions = {
      method: "Post",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ticker: ticker }),
    };
    fetch("http://localhost:8000/add_ticker", RequestOptions);
  };

  return (
    <div className="App">
      <div id="WatchListContainer">
        <div id="WatchList">
          <h1>Ticker Watch List</h1>
        </div>
        <div id="FormContainer">
          <h1>New Ticker</h1>
          <input
            id="TickerInput"
            type="text"
            value={ticker}
            onChange={handleInputChange}
          ></input>
          <button onClick={SubmitNewTicker}>Add New Ticker</button>
        </div>
      </div>
      <div id="StockDisplay"></div>
    </div>
  );
}

export default App;
