import React, { useState } from "react";
import "./App.css";

function App() {
  // Keep Track of Ticker Input
  const [ticker, setTicker] = useState("");

  // Event handler to update the state when the input changes
  const handleInputChange = () => {
    setTicker(document.getElementById("TickerInput"));
  };

  return (
    <div className="App">
      <div id="WatchListContainer">
        <div id="WatchList">
          <h1>Ticker Watch List</h1>
        </div>
        <div id="FormContainer">
          <h1>New Ticker</h1>
          <input
            id="TickerInput"
            type="text"
            value={ticker}
            onChange={handleInputChange}
          ></input>
          <button>Add New Ticker</button>
        </div>
      </div>
      <div id="StockDisplay"></div>
    </div>
  );
}

export default App;
