import React from "react";
import { Line } from "react-chartjs-2";

const StockLineChart = ({ data }) => {
  const chartData = {
    labels: data.map((entry) => entry.date), // Array of dates
    datasets: [
      {
        label: "Stock Prices",
        data: data.map((entry) => entry.price), // Array of stock prices
        borderColor: "rgba(75,192,192,1)",
        borderWidth: 2,
        pointRadius: 0,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: "time",
        time: {
          unit: "day", // Adjust as needed
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return <Line data={chartData} options={options} />;
};

export default StockLineChart;
