import React, { useState } from "react";
import "./StockButton.css";

const StockButton = (props: { stock_symbol: string; active: boolean }) => {
  // Keep Track of Button Status
  const [buttonStatus, setButtonStatus] = useState(props.active);
  // Default Class for Button
  let className = "button";

  // If Active change Class Name
  if (props.active) {
    className += "-active";
  }

  // OnClick Set Active
  const onClick = () => {
    setButtonStatus(!buttonStatus);
  };

  return (
    <button className={className} disabled={buttonStatus}>
      <h2>{props.stock_symbol}</h2>
    </button>
  );
};

export default StockButton;
